function validateForm(url) {
	
    var msisdn = jQuery('#msisdn').val();
    var formkey = jQuery('#form_key').val();
    var characterReg = msisdn.substring(0, 2);
    var error = 0;
    if (jQuery('#msisdn').val() == "" || jQuery('#msisdn').val() == null) {
        jQuery('#err_bg').addClass('err_bg');
        jQuery('#err_txt').addClass('chng_clr');
        jQuery('#err_heading').text("Sorry!");
        jQuery('#err_message').text("Please enter Mobile Phone Number");
        error++;
    }
    else if (isNaN(msisdn)) {
        jQuery('#err_bg').addClass('err_bg');
        jQuery('#err_txt').addClass('chng_clr');
        jQuery('#err_heading').text("Sorry!");
        jQuery('#err_message').text("Enter digits only");
        error++;
    }
    else if (characterReg != "03") {
        jQuery('#err_bg').addClass('err_bg');
        jQuery('#err_txt').addClass('chng_clr');
        jQuery('#err_heading').text("Sorry!");
        jQuery('#err_message').text("First two digits must be 03");
        error++;
    }
    else if (msisdn.length < 11) {
        jQuery('#err_bg').addClass('err_bg');
        jQuery('#err_txt').addClass('chng_clr');
        jQuery('#err_heading').text("Sorry!");
        jQuery('#err_message').text("MSISDN length must be 11 digits");
        error++;
    }
    else {
		jQuery(".slo_loader").css("display", "block");
        jQuery('#err_heading').text("");
        jQuery('#err_message').text("");
        jQuery('#err_bg').removeClass("err_bg");
        jQuery('#err_txt').removeClass('chng_clr');
        jQuery('#err_bg').removeClass('succes');

		jQuery('#popup_err_heading').text("");
        jQuery('#popup_err_message').text("");
        jQuery('#popup_err_bg').removeClass("popup_err_bg");
        jQuery('#popup_err_txt').removeClass('popup_chng_clr');
        jQuery('#popup_err_bg').removeClass('popup_succes');
    }

    if (error != 0) {
        return false;
    }
    

    jQuery.ajax({
        url: url,
        type: 'POST',
        dataType: "json",
        data: {
            msisdn: msisdn,
            formkey: formkey
        },
        success: function (response) {
            if (response.hasError) {
            	jQuery(".slo_loader").css("display", "none");
            	jQuery('#err_bg').removeClass('succes');
                jQuery('#err_bg').addClass('err_bg');
                jQuery('#err_txt').removeClass('succes');
                jQuery('#err_txt').addClass('chng_clr');
                jQuery('#err_heading').text("Sorry!");
                jQuery('#err_message').text(response.description);
            } else {
            	jQuery('#msisdn').prop('disabled', 'disabled');
            	jQuery('#checkSloButton').css('display', 'none');
            	jQuery('#activateSloButton').css('display', 'block');
            	jQuery(".slo_loader").css("display", "none");
                jQuery('#err_bg').addClass('err_bg');
                jQuery('#err_bg').addClass('succes');
                jQuery('#err_txt').addClass('succes');
                jQuery('#err_heading').text("Congratulations!");
                jQuery('#err_message').text(response.description);
            }
        },
        error: function (xhr, status, error) {
        	jQuery(".slo_loader").css("display", "none");
            console.log("error: " + error.message);
        }
    });
}

